mapper.select:
SELECT userEmail
FROM user
WHERE userName = #{userName}
  AND userSSN = #{userSSN}

SELECT userId
FROM user
WHERE userEmail = #{userEmail}
  AND userPhone = #{userPhone}

mapper.update:
UPDATE user
SET userPw = #{newPassword}
WHERE userId = #{userId}

UPDATE user
SET userPw = #{newPassword}
WHERE userId = #{userId}

UPDATE user
userName = #{user.userName}, userNickname = #{user.userNickname}, userPhone = #{user.userPhone}, userEmail = #{user.userEmail}, userAddress = #{user.userAddress}, userGender = #{user.userGender},
WHERE userId = #{userId}

mapper.select:
SELECT count(*)
FROM user
WHERE userEmail = #{userEmail}

SELECT count(*)
FROM user
WHERE userPhone = #{userPhone}

SELECT count(*)
FROM user
WHERE userSSN = #{userSSN}
